glsl3.vert:
precision mediump float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};

in vec3 a_position;
in vec3 a_normal;
in vec2 a_texCoord;

out vec2 uvw;
out vec2 uv0;
out vec2 uv1;
out vec2 uv2;
out vec2 uv3;
out vec3 diffuse;

uniform TexCoords {
  vec4 UVScale;
};

vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matWorld * pos;
  pos = cc_matViewProj * pos;

  uvw = a_texCoord;
  uv0 = a_position.xz * UVScale.x;
  uv1 = a_position.xz * UVScale.y;
  uv2 = a_position.xz * UVScale.z;
  uv3 = a_position.xz * UVScale.w;

  vec3 L = normalize(-cc_mainLitDir.xyz);
  vec3 N = a_normal;

  float fAmb = dot(N, vec3(0.0, -1.0, 0.0)) * 0.5 + 0.5;
  vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb) * cc_ambientSky.w;

  diffuse = ambDiff + vec3(dot(N, L));

  return pos;
}

void main() { gl_Position = vert(); }

glsl3.frag:
precision mediump float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));
}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));
}

vec4 CCFragOutput(vec4 color) {
  #if CC_USE_HDR
    color.rgb = mix(color.rgb, SRGBToLinear(color.rgb) * cc_exposure.w, vec3(cc_exposure.z));
	#endif
	return color;
}

  in vec2 uvw;
  in vec2 uv0;
  in vec2 uv1;
  in vec2 uv2;
  in vec2 uv3;
  in vec3 diffuse;

  uniform sampler2D weightMap;
  uniform sampler2D detailMap0;
  uniform sampler2D detailMap1;
  uniform sampler2D detailMap2;
  uniform sampler2D detailMap3;

vec4 frag () {
  vec4 color = vec4(0, 0, 0, 0);

  #if LAYERS == 1
    color = texture(detailMap0, uv0);
  #elif LAYERS == 2
    vec4 w = texture(weightMap, uvw);

    color += texture(detailMap0, uv0) * w.r;
    color += texture(detailMap1, uv1) * w.g;
  #elif LAYERS == 3
    vec4 w = texture(weightMap, uvw);

    color += texture(detailMap0, uv0) * w.r;
    color += texture(detailMap1, uv1) * w.g;
    color += texture(detailMap2, uv2) * w.b;
  #elif LAYERS == 4
    vec4 w = texture(weightMap, uvw);

    color += texture(detailMap0, uv0) * w.r;
    color += texture(detailMap1, uv1) * w.g;
    color += texture(detailMap2, uv2) * w.b;
    color += texture(detailMap3, uv3) * w.a;
  #else
    color = texture(detailMap0, uv0);
  #endif

  color.rgb *= diffuse;

  return CCFragOutput(color);
}

out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

