glsl3.vert:
precision mediump float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

in vec2 a_texCoord;
in vec3 a_position;
out vec2 uv;
out vec3 pos_w;

vec4 vert () {
  uv = a_texCoord;
  pos_w = a_position;
  vec2 forward = vec2(cc_matView[0][2], cc_matView[2][2]);
  float dist = abs(cc_cameraPos.y);
  vec2 scale = vec2(1.0 - a_texCoord.y, a_texCoord.y);
  pos_w.xz += scale * vec2((a_texCoord.x * 2.0 - 1.0) * dist * 0.002);

  vec4 pos = cc_matViewProj * vec4(pos_w, 1.0);
  pos.z += 0.00011;
  return pos;
}

void main() { gl_Position = vert(); }

glsl3.frag:
precision mediump float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));
}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));
}

vec4 CCFragOutput(vec4 color) {
  #if CC_USE_HDR
    color.rgb = mix(color.rgb, SRGBToLinear(color.rgb) * cc_exposure.w, vec3(cc_exposure.z));
	#endif
	return color;
}

in vec2 uv;
in vec3 pos_w;

vec4 frag () {
  float alpha = sqrt(0.5 - abs(uv.x - 0.5));
  float scale = abs(normalize(cc_cameraPos.xyz - pos_w).y);
  if (scale < 0.5) alpha *= max(0.3, scale / 0.5);
  return CCFragOutput(vec4(0.5, 0.5, 0.5, alpha));
}

out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

