glsl3.vert:
precision mediump float;
uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

in vec2 a_position;

in vec4 a_color;
out vec4 color;

vec4 vert () {
  vec4 pos = vec4(a_position, 0, 1);

  pos = cc_matViewProj * cc_matWorld * pos;
  color = a_color;

  return pos;
}

void main() { gl_Position = vert(); }

glsl3.frag:
precision mediump float;
in vec4 color;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));
}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));
}

vec4 CCFragOutput(vec4 color) {
  #if CC_USE_HDR
    color.rgb = mix(color.rgb, SRGBToLinear(color.rgb) * cc_exposure.w, vec3(cc_exposure.z));
	#endif
	return color;
}

vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  o *= color;
  return CCFragOutput(o);
}

out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

